import React, { Component } from 'react'
import Newsitem from './Newsitem'
import PropTypes from 'prop-types';

export class News extends Component {

  constructor(){
    super();
    this.state={
      articles:[],
      loading:true,
      page:1,
  }
 }
 
async componentDidMount(){
  let url ="https://newsapi.org/v2/top-headlines?country=us&apiKey=5fc5961c53e8475a9896cd1982955467&page=1";
  let data = await fetch(url);
  let parsedData = await data.json();
  console.log(parsedData);
  this.setState({articles:parsedData.articles})
}

handlePreviousClick=async()=>{
  let url =`https://newsapi.org/v2/top-headlines?country=us&apiKey=5fc5961c53e8475a9896cd1982955467&page=${this.state.page - 1}`;
  let data = await fetch(url);
  let parsedData = await data.json();
  console.log(parsedData);
  this.setState({
    page: this.state.page - 1,
    articles: parsedData.articles
  })

handleNextClick=async()=>{

  let url =`https://newsapi.org/v2/top-headlines?country=us&apiKey=5fc5961c53e8475a9896cd1982955467&page=${this.state.page + 1}`;
  let data = await fetch(url);
  let parsedData = await data.json();
  console.log(parsedData);
  this.setState({
    page: this.state.page + 1,
    articles: parsedData.articles
  })
}


  render(); {
    return (
      <div className="container my-5">
        <h1>Newscape-Top Headlines</h1>
        <div className="row">
          {this.state.articles.map((element)=>{
           return <div className="col-md-4" key={element.url}>
            <Newsitem  title={element.title?element.title.slice(0,40):""} description={element.description?element.description.slice(0,80):""} imageUrl={element.urlToImage} newsUrl={element.url}/>
            </div>
           })}
       </div>
         <div className="container d-flex justify-content-between my-5">
          <button disabled={this.state.page<=1}type='button' className='btn btn-primary' onClick={this.handlePreviousClick}>&larr;Previous</button>
          <button type='button' className='btn btn-primary' onClick={this.handleNextClick}>Next&rarr;</button>

         </div>
       
      </div>
     
    );
  };
};
}
export default News;
