//   console.log(parsedData);
 
//   this.setState((prevState) => ({
//       page: prevState.page - 1,
//       articles: parsedData.articles
//    }))
 
 
//  }

handleNextClick = async () => {
  console.log("Nextttt");

  let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=35da3bda5c6447feac9600e025078c4a&page=${this.state.page + 1}`;
  let data = await fetch(url);
  let parsedData = await data.json();
  console.log(parsedData);
  this.setState({
    page: this.state.page + 1,
    articles: parsedData.articles
  });
};

  render() {
    return (
      <div className="container my-5">
        <h1>Newscape-Top Headlines</h1>
        <div className="row">
          {this.articles.map((element)=>{
           return <div className="col-md-4" key={element.url}>
            <Newsitem  title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,80):""} imageUrl={element.urlToImage} newsUrl={element.url}/>
            </div>
           })}
       </div>
         <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1}type='button' className='btn btn-primary' onClick={this.handlePreviousClick}>&larr;Previous</button>
          <button type='button' className='btn btn-primary' onClick={this.handleNextClick}>Next&rarr;</button>

         </div>
       
      </div>
     
    );
  }
}


export default News;
