import React, { useState } from 'react'
import { Link } from 'react-router-dom';


 const Signup = () => {
    const [input,setInput] = useState({
        name: "",
        emal:"",
        password:"",
    });

    // local storage 

    const handleSubmit = (e) =>{
        e.preventDefault();
        localStorage.setItem("user", "value", JSON.stringify)
    };

  return (
    <div className="details" style={{height:'500px',width:'500px'}}>
        <h2>Create an Account</h2>
        <div class="mb-3">
      <form onSubmit={handleSubmit}>
            <label for="exampleFormControlInput1" class="form-label">Your Name</label>
            <input name="name" value={input.name} onChange={(e) => setInput({...input,[e.target.name]: e.target.value})}  type="text" class="form-control" id="exampleFormControlInput1" placeholder="John"></input>
            
            <label for="exampleFormControlInput1" class="form-label">Email address</label>
            <input name="email" value={input.email} onChange={(e) => setInput({...input,[e.target.name]: e.target.value})} type="email" class="form-control" id="exampleFormControlInput1" placeholder="name@example.com"></input>

            <label for="exampleFormControlInput1" class="form-label">Password</label>
            <input name="password" value={input.password} onChange={(e) => setInput({...input,[e.target.name]: e.target.value})}  type="password" class="form-control" id="exampleFormControlInput1" placeholder="5342122"></input>
            <button type="submit" className='btn btn-sucess btn-block btn-lg '>Register</button>

            <p>Already  have an account<Link className="nav-link"  to="/login" >Login Here</Link></p>
       </form>
        </div>
    </div>
  )
}


export default Signup
