import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import '../App.css';

 const Signup = () => {
    const navigate =  useNavigate();
    const [input,setInput] = useState({
        name: "",
        emal:"",
        password:"",
    });

    // local storage 

    const handleSubmit = (e) =>{
        e.preventDefault();
        localStorage.setItem("user", "value", JSON.stringify(input));
        navigate("/login");
    };

  return (
    <div className="details" style={{height:'500px',width:'500px'}}>
        <h2>Create an Account</h2>
        <div className="mb-3">
      <form onSubmit={handleSubmit}>
            <label htmlFor="exampleFormControlInput1" className="form-label">Your Name</label>
            <input name="name" value={input.name} onChange={(e) => setInput({...input,[e.target.name]: e.target.value})}  type="text" className="form-control" id="exampleFormControlInput1" placeholder="John"></input>
            
            <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
            <input name="email" value={input.email} onChange={(e) => setInput({...input,[e.target.name]: e.target.value})} type="email" className="form-control" id="exampleFormControlInput1" placeholder="name@example.com"></input>

            <label htmlFor="exampleFormControlInput1" className="form-label">Password</label>
            <input name="password" value={input.password} onChange={(e) => setInput({...input,[e.target.name]: e.target.value})}  type="password" className="form-control" id="exampleFormControlInput1" placeholder="5342122"></input>
            <button type="submit" className='btn btn-sucess btn-block btn-lg ' id='RegisterBtn' style={{backgroundColor:'#133E87',paddingTop:'7px' }}>Register</button>

            <p>Already  have an account<Link className="nav-link"  to="/login" >Login Here</Link></p>
       </form>
        </div>
    </div>
  )
}


export default Signup
